import React, { useState } from "react";
import "../App.css";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import DropTarget from "./DropTarget";

const Tier = () => {
    const [tiers, setTiers] = useState({
        S: [],
        A: [],
        B: [],
        C: [],
        D: [],
        F: [],
    });

    const [characterPool, setCharacterPool] = useState([
        {
            id: "Kenshi",
            name: "Kenshi",
            image: "/img/Characters/icon/Kenshi.png",
            versions: ["PS2/Xbox", "Gamecube", "PSP", "Ultimate"],
        },
        {
            id: "Jade",
            name: "Jade",
            image: "/img/Characters/icon/Jade.png",
            versions: ["PS2/Xbox", "Gamecube", "PSP", "Ultimate"],
        },
    ]);

    const handleDrop = (itemId, targetTier) => {
        // Find the item in the character pool or current tiers
        let item, sourceTierKey;

        for (const tierKey in tiers) {
            item = tiers[tierKey].find((character) => character.id === itemId);
            if (item) {
                sourceTierKey = tierKey;
                break;
            }
        }

        if (!item) {
            item = characterPool.find((character) => character.id === itemId);
            sourceTierKey = "characterPool";
        }

        if (item) {
            // Remove the item from its current tier or pool
            if (sourceTierKey === "characterPool") {
                setCharacterPool((prev) =>
                    prev.filter((character) => character.id !== itemId)
                );
            } else {
                setTiers((prevTiers) => ({
                    ...prevTiers,
                    [sourceTierKey]: prevTiers[sourceTierKey].filter(
                        (character) => character.id !== itemId
                    ),
                }));
            }

            // Add the item to the target tier
            setTiers((prevTiers) => ({
                ...prevTiers,
                [targetTier]: [...prevTiers[targetTier], item],
            }));
        }
    };

    return (
        <>
            <div className="container">
                <h1>Tier List</h1>
                <DndProvider backend={HTML5Backend}>
                    <div className="tier-container">
                        {Object.keys(tiers).map((tier) => (
                            <div key={tier} className={`tier tier-${tier}`}>
                                <h2>{tier} Tier</h2>
                                <DropTarget
                                    items={tiers[tier]}
                                    onDrop={(itemId) =>
                                        handleDrop(itemId, tier)
                                    }
                                />
                            </div>
                        ))}
                    </div>
                    <div className="character-pool">
                        <h2>Character Pool</h2>
                        <DropTarget
                            items={characterPool}
                            onDrop={(itemId) =>
                                handleDrop(itemId, "characterPool")
                            }
                        />
                    </div>
                </DndProvider>
            </div>
            <div id="disclaimer">
                <p id="mod">I am not affiliated with the mod of any kind</p>
                <p id="mod">Background by Monster Enjoyer</p>
                <p id="warner">
                    Mortal Kombat: Deception was published by Midway games.
                    Mortal Kombat©, the Dragon Logo, and all character names are
                    trademarks of Warner Bros. Entertainment Inc.
                </p>
            </div>
        </>
    );
};

export default Tier;
