import React, { useState } from "react";
import "../App.css";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import DropTarget from "./DropTarget";

const Tier = () => {
    const [tiers, setTiers] = useState({
        S: [],
        A: [],
        B: [],
        C: [],
        D: [],
        F: [],
    });

    const [characterPool, setCharacterPool] = useState([
        {
            id: "Kenshi",
            name: "Kenshi",
            image: "/img/Characters/icon/Kenshi.png",
            versions: ["PS2/Xbox", "Gamecube", "PSP", "Ultimate"],
        },
        {
            id: "Jade",
            name: "Jade",
            image: "/img/Characters/icon/Jade.png",
            versions: ["PS2/Xbox", "Gamecube", "PSP", "Ultimate"],
        },
    ]);

    const handleDrop = (
        itemId,
        targetTier,
        setTargetTier,
        sourceTier,
        setSourceTier
    ) => {
        // Find the item in the source tier
        const item = sourceTier.find((item) => item.id === itemId);
        if (item) {
            // Remove the item from the source tier
            setSourceTier((prev) => prev.filter((item) => item.id !== itemId));
            // Add the item to the target tier
            setTargetTier((prev) => [...prev, item]);
        }
    };

    return (
        <>
            <div className="container">
                <h1>Tier List</h1>
                <DndProvider backend={HTML5Backend}>
                    <div className="tier-container">
                        {Object.keys(tiers).map((tier) => (
                            <div key={tier} className={`tier tier-${tier}`}>
                                <h2>{tier} Tier</h2>
                                <DropTarget
                                    items={tiers[tier]}
                                    onDrop={(itemId) =>
                                        handleDrop(itemId, tier)
                                    }
                                />
                            </div>
                        ))}
                    </div>
                    <div className="character-pool">
                        <h2>Character Pool</h2>
                        <DropTarget items={characterPool} onDrop={() => {}} />
                    </div>
                </DndProvider>
            </div>
            <div id="disclaimer">
                <p id="mod">I am not affiliated with the mod of any kind</p>
                <p id="mod">Background by Monster Enjoyer</p>
                <p id="warner">
                    Mortal Kombat: Deception was published by Midway games
                    Mortal KombatÂ©, the Dragon Logo and all characters names are
                    trademarks of Warner Bros. Entertainment Inc.
                </p>
            </div>
        </>
    );
};

export default Tier;
